const API_URL = "https://srijandubey.github.io/campus-api-mock/SRM-C1-25.json";

export const fetchDoctors = async () => {
  try {
    console.log("Fetching doctors from:", API_URL);
    const response = await fetch(API_URL);

    if (!response.ok) {
      throw new Error(`HTTP error! Status: ${response.status}`);
    }

    const data = await response.json();
    console.log("Successfully fetched doctor data:", data.length || 0, "records");

    // Check if data is an array
    if (!Array.isArray(data)) {
      console.error("API returned non-array data:", data);
      return Array.isArray(data.doctors) ? data.doctors : [];
    }

    return data;
  } catch (error) {
    console.error("Error fetching doctors:", error);
    throw error; // Re-throw the error so App component can handle it
  }
};
