import React from 'react';
import '../styles/DoctorCard.css';

const DoctorCard = ({ doctor }) => {
  // Make sure doctor has all required properties or provide defaults
  const specialities = doctor.speciality && Array.isArray(doctor.speciality) 
    ? doctor.speciality.join(', ') 
    : 'Specialty not specified';

  return (
    <div className="doctor-card" data-testid="doctor-card">
      <div className="doctor-image">
        <img src={doctor.image || 'https://via.placeholder.com/100'} alt={doctor.name} />
      </div>
      <div className="doctor-info">
        <h2 data-testid="doctor-name">{doctor.name || 'Unknown Doctor'}</h2>
        <p data-testid="doctor-specialty">{specialities}</p>
        <p data-testid="doctor-experience">{doctor.experience || 0} years of experience</p>
        <p data-testid="doctor-fee">â‚¹{doctor.fee || 0} Consultation Fee</p>
        <div className="doctor-consult-info">
          {doctor.isVideoConsultAvailable && <span className="video-consult">Video Consult Available</span>}
          {doctor.isInClinicAvailable && <span className="in-clinic">In Clinic Available</span>}
        </div>
      </div>
    </div>
  );
};

export default DoctorCard;
