import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar';
import FilterPanel from './FilterPanel';
import DoctorList from './DoctorList';
import { fetchDoctors } from '../services/api';
import { 
  filterDoctors, 
  sortDoctors, 
  getUniqueSpecialties,
  updateQueryParams,
  parseQueryParams
} from '../utils/helpers';
import '../styles/App.css';

function App() {
  const [doctors, setDoctors] = useState([]);
  const [filteredDoctors, setFilteredDoctors] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [specialties, setSpecialties] = useState([]);
  const [selectedSpecialties, setSelectedSpecialties] = useState([]);
  const [consultationType, setConsultationType] = useState('');
  const [sortBy, setSortBy] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch doctors data
  useEffect(() => {
    const getDoctors = async () => {
      try {
        setLoading(true);
        const data = await fetchDoctors();
        setDoctors(data);
        setFilteredDoctors(data);
        setSpecialties(getUniqueSpecialties(data));
        setLoading(false);
      } catch (error) {
        setError('Failed to fetch doctors data');
        setLoading(false);
      }
    };

    getDoctors();
  }, []);

  // Load filters from URL on initial load
  useEffect(() => {
    if (doctors.length > 0) {
      const params = parseQueryParams();
      setSearchTerm(params.search);
      setSelectedSpecialties(params.specialties);
      setConsultationType(params.consultation);
      setSortBy(params.sort);
    }
  }, [doctors]);

  // Apply filters when any filter changes
  useEffect(() => {
    if (doctors.length > 0) {
      // First filter
      let filtered = filterDoctors(doctors, searchTerm, selectedSpecialties, consultationType);

      // Then sort
      if (sortBy) {
        filtered = sortDoctors(filtered, sortBy);
      }

      setFilteredDoctors(filtered);

      // Update URL params
      updateQueryParams(searchTerm, selectedSpecialties, consultationType, sortBy);
    }
  }, [doctors, searchTerm, selectedSpecialties, consultationType, sortBy]);

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="App">
      <header className="App-header">
        <h1>Find a Doctor</h1>
        <SearchBar 
          doctors={doctors} 
          searchTerm={searchTerm} 
          setSearchTerm={setSearchTerm} 
        />
      </header>
      <main className="App-main">
        <aside className="App-sidebar">
          <FilterPanel 
            specialties={specialties}
            selectedSpecialties={selectedSpecialties}
            setSelectedSpecialties={setSelectedSpecialties}
            consultationType={consultationType}
            setConsultationType={setConsultationType}
            sortBy={sortBy}
            setSortBy={setSortBy}
          />
        </aside>
        <section className="App-content">
          <DoctorList doctors={filteredDoctors} />
        </section>
      </main>
    </div>
  );
}

export default App;
