import React from 'react';
import '../styles/FilterPanel.css';

const FilterPanel = ({ 
  specialties,
  selectedSpecialties,
  setSelectedSpecialties,
  consultationType,
  setConsultationType,
  sortBy,
  setSortBy
}) => {
  // Handle specialty filter change
  const handleSpecialtyChange = (specialty) => {
    if (selectedSpecialties.includes(specialty)) {
      setSelectedSpecialties(selectedSpecialties.filter(s => s !== specialty));
    } else {
      setSelectedSpecialties([...selectedSpecialties, specialty]);
    }
  };

  // Handle consultation type change
  const handleConsultationChange = (type) => {
    setConsultationType(type === consultationType ? '' : type);
  };

  // Handle sort change
  const handleSortChange = (sort) => {
    setSortBy(sort === sortBy ? '' : sort);
  };

  return (
    <div className="filter-panel">
      <div className="filter-section">
        <h3 data-testid="filter-header-speciality">Speciality</h3>
        <div className="filter-options">
          {specialties.map((specialty) => (
            <label key={specialty} className="checkbox-label">
              <input
                type="checkbox"
                checked={selectedSpecialties.includes(specialty)}
                onChange={() => handleSpecialtyChange(specialty)}
                data-testid={`filter-specialty-${specialty.replace('/', '-')}`}
              />
              {specialty}
            </label>
          ))}
        </div>
      </div>

      <div className="filter-section">
        <h3 data-testid="filter-header-moc">Consultation Mode</h3>
        <div className="filter-options">
          <label className="radio-label">
            <input
              type="radio"
              checked={consultationType === 'Video Consult'}
              onChange={() => handleConsultationChange('Video Consult')}
              data-testid="filter-video-consult"
            />
            Video Consult
          </label>
          <label className="radio-label">
            <input
              type="radio"
              checked={consultationType === 'In Clinic'}
              onChange={() => handleConsultationChange('In Clinic')}
              data-testid="filter-in-clinic"
            />
            In Clinic
          </label>
        </div>
      </div>

      <div className="filter-section">
        <h3 data-testid="filter-header-sort">Sort</h3>
        <div className="filter-options">
          <label className="radio-label">
            <input
              type="radio"
              checked={sortBy === 'fees'}
              onChange={() => handleSortChange('fees')}
              data-testid="sort-fees"
            />
            Fees (Low to High)
          </label>
          <label className="radio-label">
            <input
              type="radio"
              checked={sortBy === 'experience'}
              onChange={() => handleSortChange('experience')}
              data-testid="sort-experience"
            />
            Experience (High to Low)
          </label>
        </div>
      </div>
    </div>
  );
};

export default FilterPanel;
